mapper.update:
UPDATE goods
updated = now(), provider=#{provider}, brand_name=#{brand_name}, goods_name=#{goods_name}, goods_code=#{goods_code}, thumbnail=#{thumbnail}, market_price=#{market_price}, sell_price=#{sell_price}
WHERE goods_id = #{goods_id}

mapper.select:
SELECT * FROM goods 
WHERE goods_code = #{goods_code}
Limit 1
