mapper.select:
SELECT * FROM user 
WHERE login_id = #{login_id} and password = #{password}
Limit 1

mapper.insert:
INSERT INTO 
	board(user_id, title, content) 
	values(#{user_id}, #{title}, #{content});

mapper.update:
UPDATE board

	title=#{title},
	content=#{content}

WHERE board_id = #{board_id}
