mapper.update:
UPDATE goods
updated = now(), provider=#{provider}, brand_name=#{brand_name}, goods_name=#{goods_name}, goods_code=#{goods_code}, thumbnail=#{thumbnail}, market_price=#{market_price}, sell_price=#{sell_price}, adj_price=#{adj_price}, goods_info=#{goods_info}, use_note=#{use_note}, use_term=#{use_term}
WHERE goods_id = #{goods_id}

mapper.select:
SELECT * FROM goods 
WHERE goods_code = #{goods_code}
Limit 1

mapper.update:
UPDATE manager

	id=HEX(AES_ENCRYPT(#{id}, 'hlint')),
	pass=password(#{pass}),
	name=HEX(AES_ENCRYPT(#{name}, 'hlint')),
	role_id=#{role_id}

WHERE manager_id = #{manager_id}

UPDATE seller
company_name=#{company_name}, mid=#{mid}, password=#{password}, code=#{code}, person_name=#{person_name}, contact=#{contact}, email=#{email}, allowed_ip=#{allowed_ip}, sale_status=#{sale_status}
WHERE seller_id = #{seller_id}

mapper.select:
SELECT *
FROM seller
WHERE mid = #{mid} and password = #{password}
LIMIT 1;
